import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';

import { UserAddOutlined } from '@ant-design/icons';
import { Layout, Form, Row, Col, Input, Spin, InputNumber, Table, Modal, Button, Select, } from "antd";
const { Content } = Layout;
const { Option } = Select;

const layout = {
    labelCol: {
        span: 7,
    },
    wrapperCol: {
        span: 14,
    },
};
const validateMessages = {
    types: {
        email: 'please add a valid email!',
        number: 'please add a number!',
    },
    number: {
        range: 'age must be between 21 and 99',
    },
};

const CreateEmployee = ({ handleSubmit, handleFetching, employeeState, loading, Success }) => {
    const [visible, setVisible] = useState(false)
    const [confirmLoading, setConfirmLoading] = useState(false)
    const [search, setSearch] = useState({ words: '' });
    const [page, setPage] = useState({ current: 1 });

    useEffect(() => {
        handleFetching();
    },[handleFetching, Success])

    let data = (!loading && employeeState ? employeeState.employee.map((row, index) => ({
        Name: row.name,
        Gender: row.gender,
        Email: row.email,
        Sr: index,
        Id: row._id,
    })): [] );

     data = data.filter((item) =>
        item.Name.toLowerCase().includes(search.words.toLowerCase())
    );

     const empLength = !loading && employeeState ? employeeState.length : 0;

    const [form] = Form.useForm();
    const { validateFields, resetFields } = form;

    const showModal = () => {
        setVisible(true)
    };

    const handleOk = () => {
        validateFields()
            .then(values => {
                resetFields();
                onFinish(values);
            })
            .then(() => { setConfirmLoading(true)
                setTimeout(() => {
                    setVisible(false)
                    setConfirmLoading(false)
                }, 1000)})
            .catch(err => {
            console.log(err);
        });

    };

    const handleCancel = () => {
        resetFields();
        setVisible(false)
    };

    const onFinish = values => {
        console.log('data is here', values);
        handleSubmit(values);
    };

    const columns = [
        {
            title: "Sr.",
            dataIndex: "Sr",
            key: "Sr"
        },
        {
            title: "Name",
            dataIndex: "Name",
            key: "Name"
        },
        {
            title: "Gender",
            dataIndex: "Gender",
            key: "Gender"
        },
        {
            title: "Email",
            dataIndex: "Email",
            key: "Email"
        },
        {
            title: "Actions",
            dataIndex: "Critical",
            key: "Actions"
        },

    ];

    const handleSearch = (e) => {
        setSearch({ words: e.target.value });
        setPage({
            current: 1,
        });
    };

    const onChange = (page) => {
        setPage({
            current: page,
        });
    };

    const showTotal = (total) => {
        return `Total ${total} employees`;
    }
return (
    <Content style={{ margin: "0 16px" }}>
        <div
            className="site-layout-background"
            style={{
                padding: 24,
                minHeight: 360,
                margin: "16px 0"
            }}
        >
            <Row>
                <Col span={7}>
                    <div>
                        <Button type="primary" onClick={showModal}>
                            <UserAddOutlined />
                            Add Employee
                        </Button>
                        <Modal
                            title="Employee Registration Form"
                            visible={visible}
                            onOk={handleOk}
                            okText="Register"
                            confirmLoading={confirmLoading}
                            onCancel={handleCancel}
                            maskClosable={false}
                        >
                                <Form {...layout} form={form} name="nest-messages" onFinish={onFinish} validateMessages={validateMessages}>
                                    <Form.Item
                                        name={'name'}
                                        label="Name"
                                        rules={[
                                            {
                                                required: true,
                                            },
                                        ]}
                                    >
                                        <Input placeholder='Please Input' />
                                    </Form.Item>
                                    <Form.Item
                                        name={'email'}
                                        label="Email"
                                        rules={[
                                            {
                                                type: 'email',
                                                required: true,
                                            },
                                        ]}
                                    >
                                        <Input placeholder='Please enter your email' />
                                    </Form.Item>
                                    <Form.Item
                                        name={'age'}
                                        label="Age"
                                        rules={[
                                            {
                                                type: 'number',
                                                required: true,
                                                min: 21,
                                                max: 99,
                                            },
                                        ]}
                                    >
                                        <InputNumber />
                                    </Form.Item>
                                    <Form.Item
                                        name="gender"
                                        label="Gender"
                                        rules={[
                                            {
                                                required: true,
                                            },
                                        ]}
                                    >
                                        <Select
                                            placeholder="Select a gender"
                                            allowClear
                                        >
                                            <Option value="male">male</Option>
                                            <Option value="female">female</Option>
                                            <Option value="other">other</Option>
                                        </Select>
                                    </Form.Item>
                                    <Form.Item
                                        name={'designation'}
                                        label="Designation"
                                        rules={[
                                            {
                                                required: true,
                                            },
                                        ]}
                                    >
                                        <Input  placeholder='Please enter your designation' />
                                    </Form.Item>
                                    <Form.Item
                                        name={'department'}
                                        label="Department"
                                        rules={[
                                            {
                                                required: true,
                                            },
                                        ]}
                                    >
                                        <Input  placeholder='Please enter your department' />
                                    </Form.Item>
                                    <Form.Item name={'address'} label="Address"   rules={[
                                        {
                                            required: true,
                                        },
                                    ]}>
                                        <Input.TextArea placeholder='Please add address' />
                                    </Form.Item>
                                </Form>
                        </Modal>
                    </div>
                </Col>
                <Col span={12} style={{ paddingLeft: 180 }}>
                    <Form.Item label="Filter By" >
                        <Input placeholder="filter by name" size="small" onChange={handleSearch} />
                    </Form.Item>
                </Col>
            </Row>
            {loading ? (
                <Spin size="large" style={{ paddingTop: 100, width: 900 }} />
            ) : (
                <Table
                    rowKey={(obj) => obj.Id}
                    columns={columns}
                    dataSource={data}
                    pagination={{
                        pageSize: 7,
                        current: page.current,
                        onChange: onChange,
                        total: {empLength},
                        showTotal: showTotal
                    }}
                    scroll={{ y: 240 }}
                />
            )}
        </div>
    </Content>
);
}

CreateEmployee.defaultProps ={};

CreateEmployee.propTypes = {
    handleSubmit: PropTypes.func.isRequired,
    handleFetching: PropTypes.func.isRequired,
    employeeState: PropTypes.any,
    loading: PropTypes.bool,
    Success: PropTypes.bool,
};

CreateEmployee.styles = {};

export default CreateEmployee;
