import { connect } from 'react-redux';

import Dashboard from './view/Dashboard.view';
import { createEmployee, getAllEmployees, deleteEmployee } from './dashboard.api'

const mapStateToProps = (state) => ({
    isAuthenticated: state.getIn(['auth', 'isAuthenticated']),
    employee: state.getIn(['employee', 'employee']),
    isCreatingSuccess: state.getIn(['employee', 'isCreatingSuccess']),
    loading: state.getIn(['employee', 'loading']),
    isDeletingSuccess: state.getIn('employee', 'isDeletingSuccess'),
});

const mapDispatchToProps = (dispatch) => ({
  createEmployee: (payload) => {
    dispatch(createEmployee(payload));
  },
   getAllEmployees: () => {
    dispatch(getAllEmployees())
   },
    deleteEmployee: (id) => {
      dispatch(deleteEmployee(id))
    }
});

export default connect(mapStateToProps, mapDispatchToProps)(Dashboard);
