import { get } from 'lodash';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import React, { Fragment, useEffect } from 'react';
import { withRouter, Redirect } from 'react-router-dom';

import AppRoutes from '../../AppRoutes';
import { getUser, refreshToken } from '../auth/auth.api';
import storage from '../../common/storage';
import { AUTH_ROUTES, USER_STATE } from '../../common/constants';

const Layout = ({
  history,
  location,
  getUserFromApi,
  isAuthenticated,
    refreshToken,
}) => {

  const admin = get(storage.get('user'), 'user.typeOfId') === USER_STATE.ADMIN_FULL || USER_STATE.ADMIN_PARTIAL;
  const employee = get(storage.get('user'), 'user.typeOfId') === USER_STATE.EMPLOYEE || 0 ;

  useEffect(() => {
    if (isAuthenticated && admin && location && location.pathname) {
      history.push(
        AUTH_ROUTES.includes(location.pathname)
          ? '/dashboard'
          : location.pathname,
      );
    } else if (isAuthenticated && employee && location && location.pathname) {
      history.push(
          AUTH_ROUTES.includes(location.pathname)
              ? '/employee'
              : '/employee',
      );
    }else if (!isAuthenticated && get(storage.get('user'), 'token')) {
      getUserFromApi()
    }
    let interval;
    if (storage.get('user')) {
      interval = setInterval(() => {refreshToken()}, 840000);
    }
    return () => clearInterval(interval);
    // eslint-disable-next-line
  },[getUserFromApi, isAuthenticated, history]);

  // working with refresh token later
  // axios.interceptors.response.use(function (response) {
  //   return response;
  // }, function (error) {
  //   if(error.status === 200) {
  //     // do something when unauthorized
  //     store.dispatch(test());
  //   }
  //   return Promise.reject(error);
  // });

  return [
    <Fragment key="redirect">
      {isAuthenticated && (
        <Redirect
          to={
            AUTH_ROUTES.includes(location.pathname)
              ? '/dashboard'
              : location.pathname
          }
        />
      )}
    </Fragment>,
    <div key="app" id="app-wrapper">
      <AppRoutes />
    </div>,
  ];
};

Layout.defaultProps = {
  isAuthenticated: false,
};

Layout.propTypes = {
  isAuthenticated: PropTypes.bool,
  getUserFromApi: PropTypes.func,
  history: PropTypes.object.isRequired,
  location: PropTypes.object.isRequired,
  refreshToken: PropTypes.func,
};

const mapStateToProps = (state) => ({
  isAuthenticated: state.getIn(['auth', 'isAuthenticated']),
});

const mapDispatchToProps = (dispatch) => ({
  getUserFromApi: () => {
    dispatch(getUser());
  },
  refreshToken: () => {
    dispatch(refreshToken());
  },
});

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(Layout));
