import React, { useState } from 'react';
// import PropTypes from 'prop-types';

import { UserAddOutlined } from '@ant-design/icons';
import { Layout, Form, Row, Col, Input, Spin, InputNumber, Table, Modal, Button  } from "antd";
const { Content } = Layout;

const layout = {
    labelCol: {
        span: 8,
    },
    wrapperCol: {
        span: 16,
    },
};
const validateMessages = {
    required: `name is required!`,
    types: {
        email: `email is not validate email!`,
        number: `number is not a validate number!`,
    },
    number: {
        range: `age must be between min and max`,
    },
};

const CreateEmployee = () => {
    const [visible, setVisible] = useState(false)
    const [confirmLoading, setConfirmLoading] = useState(false)
    const loading = false;

    const showModal = () => {
        setVisible(true)
    };

    const handleOk = () => {
        setConfirmLoading(true)
        setTimeout(() => {
            setVisible(false)
            setConfirmLoading(false)
        }, 2000);
    };

    const handleCancel = () => {
        setVisible(false)
    };

    const onFinish = values => {
        console.log(values);
    };
    const columns = [
        {
            title: 'Name',
            dataIndex: 'name',
            width: 150,
        },
        {
            title: 'Age',
            dataIndex: 'age',
            width: 150,
        },
        {
            title: 'Address',
            dataIndex: 'address',
        },
    ];

    const data = [];
    for (let i = 0; i < 100; i++) {
        data.push({
            key: i,
            name: `Edward King ${i}`,
            age: 32,
            address: `London, Park Lane no. ${i}`,
        });
    }
return (
    <Content style={{ margin: "0 16px" }}>
        <div
            className="site-layout-background"
            style={{
                padding: 24,
                minHeight: 360,
                margin: "16px 0"
            }}
        >
            <Row>
                <Col span={7}>
                    <div>
                        <Button type="primary" onClick={showModal}>
                            <UserAddOutlined />
                            Add Employee
                        </Button>
                        <Modal
                            title="Add Employee"
                            visible={visible}
                            onOk={handleOk}
                            confirmLoading={confirmLoading}
                            onCancel={handleCancel}
                            style={{overflow: 'auto', height: '50vh'}}
                        >
                                <Form {...layout} name="nest-messages" onFinish={onFinish} validateMessages={validateMessages}>
                                    <Form.Item
                                        name={['user', 'name']}
                                        label="Name"
                                        rules={[
                                            {
                                                required: true,
                                            },
                                        ]}
                                    >
                                        <Input />
                                    </Form.Item>
                                    <Form.Item
                                        name={['user', 'email']}
                                        label="Email"
                                        rules={[
                                            {
                                                type: 'email',
                                            },
                                        ]}
                                    >
                                        <Input />
                                    </Form.Item>
                                    <Form.Item
                                        name={['user', 'age']}
                                        label="Age"
                                        rules={[
                                            {
                                                type: 'number',
                                                min: 0,
                                                max: 99,
                                            },
                                        ]}
                                    >
                                        <InputNumber />
                                    </Form.Item>
                                    <Form.Item name={['user', 'website']} label="Website">
                                        <Input />
                                    </Form.Item><Form.Item name={['user', 'website']} label="Website">
                                        <Input />
                                    </Form.Item><Form.Item name={['user', 'website']} label="Website">
                                        <Input />
