import React, { useState } from 'react';
// import PropTypes from 'prop-types';

import { Layout, Form, Row, Col, Input, Spin } from "antd";
import { Table, Modal, Button } from "antd";

const { Content } = Layout;


const CreateEmployee = () => {
    const [modal, setModal] = useState({ ModalText: 'Content of the modal', visible: false, confirmLoading: false,})
    const loading = false;

    const showModal = () => {
        this.setState({
            visible: true,
        });
    };

    const handleOk = () => {
        this.setState({
            ModalText: 'The modal will be closed after two seconds',
            confirmLoading: true,
        });
        setTimeout(() => {
            this.setState({
                visible: false,
                confirmLoading: false,
            });
        }, 2000);
    };

    const handleCancel = () => {
        console.log('Clicked cancel button');
        this.setState({
            visible: false,
        });
    };
    const columns = [
        {
            title: 'Name',
            dataIndex: 'name',
            width: 150,
        },
        {
            title: 'Age',
            dataIndex: 'age',
            width: 150,
        },
        {
            title: 'Address',
            dataIndex: 'address',
        },
    ];

    const data = [];
    for (let i = 0; i < 100; i++) {
        data.push({
            key: i,
            name: `Edward King ${i}`,
            age: 32,
            address: `London, Park Lane no. ${i}`,
        });
    }
return (
    <Content style={{ margin: "0 16px" }}>
        <div
            className="site-layout-background"
            style={{
                padding: 24,
                minHeight: 360,
                margin: "16px 0"
            }}
        >
            <Row>
                <Col span={7}>
                    <form >
                        <Form.Item label="Sort By">
                            <select
                                className="form-control form-control-sm"
                            >
                                <option value="confirmed">Confirmed</option>
                                <option value="critical">Critical</option>
                                <option value="deaths">Deaths</option>
                                <option value="recovered">Recovered</option>
                            </select>
                        </Form.Item>
                    </form>
                </Col>
                <Col span={12} style={{ paddingLeft: 180 }}>
                    <Form.Item label="Search By">
                        <Input placeholder="enter country name"/>
                    </Form.Item>
                </Col>
            </Row>
            {loading ? (
                <Spin size="large" style={{ paddingTop: 100, width: 900 }} />
            ) : (
                <Table
                    columns={columns}
                    dataSource={data}
                    pagination={{ pageSize: 20 }}
                    scroll={{ y: 240 }}
                />
            )}
        </div>
    </Content>
);
}

CreateEmployee.defaultProps ={};

CreateEmployee.propTypes = {};

CreateEmployee.styles = {};

export default CreateEmployee;
