import React, {useEffect, useState} from 'react';
import PropTypes from 'prop-types';
import './CRUD.Employee.css';
import Moment from 'react-moment'


import {
    DeleteOutlined,
    ExclamationCircleOutlined,
    FormOutlined,
    PlusOutlined,
    UserAddOutlined
} from '@ant-design/icons';
import {
    Button,
    Col,
    Form,
    Input,
    InputNumber,
    Layout,
    message,
    Modal,
    Radio,
    Row,
    Select,
    Spin,
    Table,
    Typography,
    Upload
} from "antd";
import {get} from "lodash";
import storage from "../../../common/storage";
import {USER_STATE} from '../../../common/constants';

const { Content } = Layout;
const { confirm } = Modal;
const { Option } = Select;
const { Text } = Typography;


const layout = {
    labelCol: {
        span: 7,
    },
    wrapperCol: {
        span: 14,
    },
};

const getBase64 = (img, callback) =>  {
    const reader = new FileReader();
    reader.addEventListener('load', () => callback(reader.result));
    reader.readAsDataURL(img);
}

const CRUDEmployee = ({
                            handleSubmit,
                            handleFetching,
                            employeeState,
                            loading,
                            Success,
                            handleDeletion,
                            deletion,
                            getSingleEmployee,
                            singleEmployeeState,
                            updateSingleEmployee,
                            onUpdate,

 }) => {
    const [visible, setVisible] = useState(false)
    const [visibleUpdate, setVisibleUpdate] = useState(false)
    const [visibleView, setVisibleView] = useState(false)
    const [confirmLoading, setConfirmLoading] = useState(false)
    const [search, setSearch] = useState({ words: '' });
    const [page, setPage] = useState({ current: 1 });
    const [id, setId] = useState('')
    const [imgPreview, setImgPreview] = useState(null);
    const [imgData, setImgData] = useState(null)
    const [radioBtn, setRadioBtn] = useState('')

    const typeOfId = get(storage.get('user'), 'user.typeOfId');

    useEffect(() => {
        handleFetching();
    },[handleFetching, Success, deletion, onUpdate,])

    useEffect(() => {
        if (id) {
            getSingleEmployee(id)
        }
    }, [getSingleEmployee, id,]);


    let data = (!loading && employeeState ? employeeState.employee.map((row, index) => ({
        Name: row.name.charAt(0).toUpperCase() + row.name.slice(1),
        Gender: row.gender.charAt(0).toUpperCase() + row.gender.slice(1),
        Photo: row.photo,
        Department: row.department,
        joinDate: <Moment format={"YYYY-MM-DD HH:mm"}>{row.joinDate}</Moment>,
        Sr: index,
        Id: row._id,
    })): [] );

    data = data.filter((item) => radioBtn ? item.Department.toLowerCase().includes(radioBtn) : item.Name.toLowerCase().includes(search.words.toLowerCase()));

     const empLength = !loading && employeeState ? employeeState.length : 0;
     const [form] = Form.useForm();
     const { validateFields, resetFields, setFieldsValue, scrollToField } = form;

    setTimeout(() => {
        if (id && singleEmployeeState && singleEmployeeState.employee ) {
            setFieldsValue({
                name: singleEmployeeState.employee.name,
                email: singleEmployeeState.employee.email,
                gender: singleEmployeeState.employee.gender,
                age: singleEmployeeState.employee.age,
                designation: singleEmployeeState.employee.designation,
                department: singleEmployeeState.employee.department,
                address: singleEmployeeState.employee.address,
            })
        } else {
            resetFields();
        }
    }, 50)

    const showDeleteConfirm = (id) => {
        confirm({
            title: 'Are you sure delete this employee?',
            icon: <ExclamationCircleOutlined />,
            content: 'After this operation employee will successfully deleted from database',
            okText: 'Yes',
            okType: 'danger',
            cancelText: 'No',
            onOk() {
                handleDeletion(id)
            },
        });
    }

    const handleOkForRegistration = () => {
        validateFields()
            .then(values => {
                resetFields();
                onFinish(values);
            })
            .then(() => { setConfirmLoading(true)
                setTimeout(() => {
                    setVisible(false)
                    setImgPreview(null)
                    setConfirmLoading(false)
                }, 1000)})
            .catch(err => {
                console.log(err);
            });

    };

    const handleCancelForRegistration = () => {
        setImgPreview(null)
        setVisible(false)
    };

    const onFinish = values => {
        handleSubmit(values, imgData)
    };

    const updateEmployee = (values) => {
        updateSingleEmployee(id, values, imgData)
    }

    const handleOkForUpdate = () => {
        validateFields()
            .then((values) => {
                resetFields();
                updateEmployee(values);
            })
            .then(() => { setConfirmLoading(true)
                setTimeout(() => {
                    setVisible(false)
                    setId(undefined)
                    setImgPreview(null)
                    setConfirmLoading(false)
                }, 1000)})
            .catch(err => {
            console.log(err);
        });
        setVisibleUpdate( false )
    };

    const handleCancelForUpdate = () => {
        setVisibleUpdate( false )
        setImgPreview(null)
        setId(undefined);
    };

    if (id === undefined ) {
        setTimeout(() => {
            resetFields();
        },50)
    }

    const showModalForRegistration= () => {
        setVisible(true)
    };

    const showModalForUpdate = () => {
        setVisibleUpdate(true)
    }

    const onFinishFailed = ({ errorFields }) => {
        scrollToField(errorFields[0].name);
    };

    const showModalForViewEmployee = () => {
       setVisibleView(true)
    };

    const handleCancelForViewEmployee = () => {
        setVisibleView(false)
        setId(undefined)
    };

    const columns = [
        {
            title: "Sr.",
            dataIndex: "Sr",
            key: "Sr"
        },
        {
            title: "img",
            dataIndex: "Photo",
            key: "img",
            render: record => <img alt='test' src={record} height={30}/>
        },
        {
            title: "Name",
            dataIndex: "Name",
            key: "Name",
            render: (text, record) => (
                <span>
                hello
                <p onClick={() => {
                showModalForViewEmployee()
                setId(record.Id)
            }} style={{cursor: 'pointer', color: 'blue'}}>{text}</p>
                </span>
            ),
        },
        {
            title: "Gender",
            dataIndex: "Gender",
            key: "Gender"
        },
        {
            title: "JoinDate",
            dataIndex: "joinDate",
            key: "JoinDate"
        },
        {
            title: "Actions",
            key: "Actions",
            render: (record) => (
                <span style={{ paddingLeft: '10px' }}>
                   <FormOutlined style={{color: 'green'}} onClick={() => {
                       showModalForUpdate()
                       setId(record.Id)
                   }} />
                    {typeOfId === USER_STATE.ADMIN_FULL ?
                        <DeleteOutlined style={{color: 'red', paddingLeft: '15px'}} onClick={() => showDeleteConfirm(record.Id)} />
                        : null }
                </span>
            ),
        },

    ];

    const handleSearch = (e) => {
        console.log(e.target)
        setSearch({ words: e.target.value });
        setPage({
            current: 1,
        });
    };

    const onChange = (page) => {
        setPage({
            current: page,
        });
    };

    const showTotal = (total) => {
        return `Total ${total} employees`;
    }

    const handleChange = ({fileList}) => {
        const file = fileList.slice(-1)
        const {type, size} = file[0].originFileObj
        const isJpgOrPng = type === 'image/jpeg' || type === 'image/png' || type === 'image/jpg' ;
        const isLt2M = size / 1024 / 1024 <= 2;
        if (!isJpgOrPng) {
            message.error('You can only upload JPG/PNG file!');
        }else if (!isLt2M) {
            message.error('Image must be smaller than or equal to 2MB!');
        } else {
            setImgData(file[0].originFileObj);
            getBase64(file[0].originFileObj, imageUrl =>
                setImgPreview(imageUrl)
            );
        }

    };
    const uploadButton = (
        <div className="ant-upload-text"><PlusOutlined />Image</div>
    );

    const onRadioChange = e => setRadioBtn(e.target.value)

return (
    <Content style={{ margin: "0 16px" }}>
        <div
            className="site-layout-background"
            style={{
                padding: 24,
                minHeight: 360,
                margin: "16px 0"
            }}
        >
            <Row>
                <Col span={10}>
                    <div>
                        {typeOfId === USER_STATE.ADMIN_FULL ?
                            <Button type="primary" onClick={showModalForRegistration}>
                                <UserAddOutlined />
                                Add Employee
                            </Button> : null}
                        <Modal
                            getContainer={false}
                            title={visible ? "Employee Registration Form" : 'Update Employee'}
                            visible={visible ? visible : visibleUpdate}
                            onOk={visible ? handleOkForRegistration : handleOkForUpdate}
                            okText={visible ? "Register" : 'Update'}
                            confirmLoading={confirmLoading}
                            onCancel={visible ? handleCancelForRegistration : handleCancelForUpdate}
                            maskClosable={false}
                        >
                            <Row>
                                <Col span={10} />
                                <Col span={14}>
                                    <Upload
                                        name="file"
                                        listType="picture-card"
                                        showUploadList={false}
                                        beforeUpload={() => false}
                                        onChange={handleChange}
                                    >
                                        {imgPreview ? <img src={imgPreview} alt="avatar" style={{ width: '100%' }} /> : uploadButton}
                                    </Upload>
                                </Col>
                            </Row>

                                <Form {...layout}
                                      form={form}
                                      name="nest-messages"
                                      onFinish={visible ? onFinish : updateEmployee}
                                      onFinishFailed={onFinishFailed}
                                >
                                    <Form.Item
                                        name={'name'}
                                        label="Name"
                                        rules={[
                                            {
                                                required: true,
                                            },
                                        ]}
                                    >
                                        <Input placeholder='Please Input' />
                                    </Form.Item>
                                    <Form.Item
                                        name={'email'}
                                        label="Email"
                                        rules={[
                                            {
                                                type: 'email',
                                                required: true,
                                            },
                                        ]}
                                    >
                                        <Input placeholder='Please enter your email' />
                                    </Form.Item>
                                    <Form.Item
                                        name="age"
                                        label="Age"
                                        rules={[
                                            {
                                                required: true,
                                            },
                                        ]}
                                    >
                                        <InputNumber  placeholder='age' />
                                    </Form.Item>
                                    <Form.Item
                                        name="gender"
                                        label="Gender"
                                        rules={[
                                            {
                                                required: true,
                                            },
                                        ]}
                                    >
                                        <Select
                                            placeholder="Select a gender"
                                            allowClear
                                        >
                                            <Option value="male">male</Option>
                                            <Option value="female">female</Option>
                                            <Option value="other">other</Option>
                                        </Select>
                                    </Form.Item>
                                    <Form.Item
                                        name={'designation'}
                                        label="Designation"
                                        rules={[
                                            {
                                                required: true,
                                            },
                                        ]}
                                    >
                                        <Input  placeholder='Please enter your designation' />
                                    </Form.Item>
                                    <Form.Item
                                        name={'department'}
                                        label="Department"
                                        rules={[
                                            {
                                                required: true,
                                            },
                                        ]}
                                    >
                                        <Input  placeholder='Please enter your department' />
                                    </Form.Item>
                                    <Form.Item name={'address'} label="Address"   rules={[
                                        {
                                            required: true,
                                        },
                                    ]}>
                                        <Input.TextArea placeholder='Please add address' />
                                    </Form.Item>
                                </Form>
                        </Modal>

                    </div>
                    <Modal
                        title='Viewing Employee'
                        visible={visibleView}
                        maskClosable={false}
                        onCancel={handleCancelForViewEmployee}
                        footer={null}
                    >
                        {!singleEmployeeState ?
                            <Spin tip="Loading..."  /> :
                            <div>
                                <Text strong>Name: </Text><Text>{singleEmployeeState && singleEmployeeState.employee.name}</Text>
                                <br />
                                <Text strong>Age: </Text><Text>{singleEmployeeState && singleEmployeeState.employee.age}</Text>
                                <br />
                                <Text strong>Gender: </Text><Text>{singleEmployeeState && singleEmployeeState.employee.gender}</Text>
                                <br />
                                <Text strong>Email: </Text><Text>{singleEmployeeState && singleEmployeeState.employee.email}</Text>
                                <br />
                                <Text strong>JoinDate: </Text><Text>{singleEmployeeState && singleEmployeeState.employee.joinDate.split('T')[0]}</Text>
                                <br />
                                <Text strong>Department: </Text><Text>{singleEmployeeState && singleEmployeeState.employee.department}</Text>
                                <br />
                                <Text strong>Designation: </Text><Text>{singleEmployeeState && singleEmployeeState.employee.designation}</Text>
                                <br />
                                <Text strong>Address: </Text><Text>{singleEmployeeState && singleEmployeeState.employee.address}</Text>
                            </div>
                        }
                    </Modal>
                </Col>
                <Col span={8} >
                    <Radio.Group onChange={onRadioChange}>
                        <Radio.Button value="front-end">Front-End</Radio.Button>
                        <Radio.Button value="back-end">Back-End</Radio.Button>
                        <Radio.Button value="dev-ops">Dev-Ops</Radio.Button>
                        <Radio.Button value=''>All</Radio.Button>
                    </Radio.Group>
                </Col>
                <Col span={6} >
                    <Form.Item>
                        <Input placeholder="filter by name"  onChange={handleSearch} />
                    </Form.Item>
                </Col>
            </Row>
            {loading ? (
                <Spin size="large" tip="Loading..." style={{ paddingTop: 100, width: 900 }} />
            ) : (
                <Table
                    rowKey={(obj) => obj.Id}
                    columns={columns}
                    dataSource={data}
                    pagination={
                        {
                        current: page.current,
                        onChange: onChange,
                        total: {empLength},
                        showTotal: showTotal,
                        showQuickJumper: true,
                        showSizeChanger: true,
                    }}
                    scroll={{ y: 240 }}
                />
            )}
        </div>
    </Content>
);
}

CRUDEmployee.defaultProps ={};

CRUDEmployee.propTypes = {
    handleSubmit: PropTypes.func.isRequired,
    handleFetching: PropTypes.func.isRequired,
    handleDeletion: PropTypes.func.isRequired,
    getSingleEmployee: PropTypes.func.isRequired,
    updateSingleEmployee: PropTypes.func.isRequired,
    employeeState: PropTypes.any,
    singleEmployeeState: PropTypes.any,
    deletion: PropTypes.bool,
    loading: PropTypes.bool,
    Success: PropTypes.bool,
    onUpdate: PropTypes.bool,
};

CRUDEmployee.styles = {};

export default CRUDEmployee;
