import React, { useState } from 'react';
// import PropTypes from 'prop-types';

import { UserAddOutlined } from '@ant-design/icons';
import { Layout, Form, Row, Col, Input, Spin } from "antd";
import { Table, Modal, Button } from "antd";

const { Content } = Layout;


const CreateEmployee = () => {
    const [modal, setModal] = useState({ ModalText: 'Content of the modal'})
    const [visible, setVisible] = useState(false)
    const [confirmLoading, setConfirmLoading] = useState({ confirmLoading: false })
    const loading = false;

    const showModal = () => {
        console.log('i am clicked')
        setVisible(true)
    };

    const handleOk = () => {
        setModal({ModalText: 'The modal will be closed after two seconds'});
        setConfirmLoading({confirmLoading: true})
        setTimeout(() => {
            setVisible(false)
            setConfirmLoading({confirmLoading: false})
        }, 2000);
    };

    const handleCancel = () => {
        setVisible(false)
    };
    const columns = [
        {
            title: 'Name',
            dataIndex: 'name',
            width: 150,
        },
        {
            title: 'Age',
            dataIndex: 'age',
            width: 150,
        },
        {
            title: 'Address',
            dataIndex: 'address',
        },
    ];

    const data = [];
    for (let i = 0; i < 100; i++) {
        data.push({
            key: i,
            name: `Edward King ${i}`,
            age: 32,
            address: `London, Park Lane no. ${i}`,
        });
    }
return (
    <Content style={{ margin: "0 16px" }}>
        <div
            className="site-layout-background"
            style={{
                padding: 24,
                minHeight: 360,
                margin: "16px 0"
            }}
        >
            <Row>
                <Col span={7}>
                    <div>
                        <Button type="primary" onClick={showModal}>
                            <UserAddOutlined />
                            Add Employee
                        </Button>
                        <Modal
                            title="Title"
                            visible={visible}
                            onOk={handleOk}
                            confirmLoading={confirmLoading}
                            onCancel={handleCancel}
                        >
                            <p>{modal.ModalText}</p>
                        </Modal>
                    </div>
                </Col>
                <Col span={12} style={{ paddingLeft: 180 }}>
                    <Form.Item label="Search By">
                        <Input placeholder="search by name"/>
                    </Form.Item>
                </Col>
            </Row>
            {loading ? (
                <Spin size="large" style={{ paddingTop: 100, width: 900 }} />
            ) : (
                <Table
                    columns={columns}
                    dataSource={data}
                    pagination={{ pageSize: 20 }}
                    scroll={{ y: 240 }}
                />
            )}
        </div>
    </Content>
);
}

CreateEmployee.defaultProps ={};

CreateEmployee.propTypes = {};

CreateEmployee.styles = {};

export default CreateEmployee;
