import { Map } from 'immutable';
import { createReducer } from 'redux-act';

import {
    requestEmployeeRecord,
    EmployeeRecordSuccess,
    EmployeeRecordFailure,
}
from './Employee.actions'

import {
    FETCHED,
    FETCHING,
    FAILURE,
} from '../../common/constants';


const defaultState = Map( {
    isFetching: '',
    isFetched: false,
})

const reducer = createReducer({
    [requestEmployeeRecord]: (state) => {
        return state
            .set('isFetching', FETCHING)
            .set('isFetched', false);
    },
    [EmployeeRecordSuccess]: (state) => {
        return state
            .set('isFetching', FETCHED)
            .set('isFetched', true);
    },
    [EmployeeRecordFailure]: (state) => {
        return state
            .set('isFetching', FAILURE)
            .set('isFetched', false);
    },
}, defaultState);

export default reducer;
