import React, { useState } from 'react';
import PropTypes from 'prop-types';

import { UserAddOutlined } from '@ant-design/icons';
import { Layout, Form, Row, Col, Input, Spin, InputNumber, Table, Modal, Button, Select  } from "antd";
const { Content } = Layout;
const { Option } = Select;

const layout = {
    labelCol: {
        span: 7,
    },
    wrapperCol: {
        span: 14,
    },
};
const validateMessages = {
    types: {
        email: 'please add a valid email!',
        number: 'please add a number!',
    },
    number: {
        range: 'age must be between 0 and 99',
    },
};

const CreateEmployee = ({createEmployee}) => {
    const [visible, setVisible] = useState(false)
    const [confirmLoading, setConfirmLoading] = useState(false)
    const loading = false;

    const [form] = Form.useForm();
    const { validateFields, resetFields } = form;

    const showModal = () => {
        setVisible(true)
    };

    const handleOk = () => {
        validateFields()
            .then(values => {
                resetFields();
                onFinish(values);
            })
            .then(() => { setConfirmLoading(true)
                setTimeout(() => {
                    setVisible(false)
                    setConfirmLoading(false)
                }, 2000)})
            .catch(err => {
            console.log(err);
        });

    };

    const handleCancel = () => {
        resetFields();
    };

    const onFinish = values => {
        console.log('data is here', values);
        createEmployee(values);
    };

    const columns = [
        {
            title: 'Name',
            dataIndex: 'name',
            width: 150,
        },
        {
            title: 'Age',
            dataIndex: 'age',
            width: 150,
        },
        {
            title: 'Address',
            dataIndex: 'address',
        },
    ];

    const data = [];
    for (let i = 0; i < 100; i++) {
        data.push({
            key: i,
            name: `Edward King ${i}`,
            age: 32,
            address: `London, Park Lane no. ${i}`,
        });
    }
return (
    <Content style={{ margin: "0 16px" }}>
        <div
            className="site-layout-background"
            style={{
                padding: 24,
                minHeight: 360,
                margin: "16px 0"
            }}
        >
            <Row>
                <Col span={7}>
                    <div>
                        <Button type="primary" onClick={showModal}>
                            <UserAddOutlined />
                            Add Employee
                        </Button>
                        <Modal
                            title="Add Employee"
                            visible={visible}
                            onOk={handleOk}
                            okText="Add"
                            confirmLoading={confirmLoading}
                            onCancel={handleCancel}
                            maskClosable={false}
                            afterClose={Modal.success}
                        >
                                <Form {...layout} form={form} name="nest-messages" onFinish={onFinish} validateMessages={validateMessages}>
                                    <Form.Item
                                        name={'name'}
                                        label="Name"
                                        rules={[
                                            {
                                                required: true,
                                            },
                                        ]}
                                    >
                                        <Input placeholder='Please Input' />
                                    </Form.Item>
                                    <Form.Item
                                        name={'email'}
                                        label="Email"
                                        rules={[
                                            {
                                                type: 'email',
                                                required: true,
                                            },
                                        ]}
                                    >
                                        <Input placeholder='Please enter your email' />
                                    </Form.Item>
                                    <Form.Item
                                        name={'age'}
                                        label="Age"
                                        rules={[
                                            {
                                                type: 'number',
                                                required: true,
                                                min: 0,
                                                max: 99,
                                            },
                                        ]}
                                    >
                                        <InputNumber />
                                    </Form.Item>
                                    <Form.Item
                                        name="gender"
                                        label="Gender"
                                        rules={[
                                            {
                                                required: true,
                                            },
                                        ]}
                                    >
                                        <Select
                                            placeholder="Select a gender"
                                            allowClear
                                        >
                                            <Option value="male">male</Option>
                                            <Option value="female">female</Option>
                                            <Option value="other">other</Option>
                                        </Select>
                                    </Form.Item>
                                    <Form.Item
                                        name={'designation'}
                                        label="Designation"
                                        rules={[
                                            {
                                                required: true,
                                            },
                                        ]}
                                    >
                                        <Input  placeholder='Please enter your designation' />
                                    </Form.Item>
                                    <Form.Item
                                        name={'department'}
                                        label="Department"
                                        rules={[
                                            {
                                                required: true,
                                            },
                                        ]}
                                    >
                                        <Input  placeholder='Please enter your department' />
                                    </Form.Item>
                                    <Form.Item name={'address'} label="Address"   rules={[
                                        {
                                            required: true,
                                        },
                                    ]}>
                                        <Input.TextArea placeholder='Please add address' />
                                    </Form.Item>
                                </Form>
                        </Modal>
                    </div>
                </Col>
                <Col span={12} style={{ paddingLeft: 180 }}>
                    <Form.Item label="Search By">
                        <Input placeholder="search by name"/>
                    </Form.Item>
                </Col>
            </Row>
            {loading ? (
                <Spin size="large" style={{ paddingTop: 100, width: 900 }} />
            ) : (
                <Table
                    columns={columns}
                    dataSource={data}
                    pagination={{ pageSize: 20 }}
                    scroll={{ y: 240 }}
                />
            )}
        </div>
    </Content>
);
}

CreateEmployee.defaultProps ={};

CreateEmployee.propTypes = {
    createEmployee: PropTypes.func.isRequired,
};

CreateEmployee.styles = {};

export default CreateEmployee;
