import React, { useState }  from 'react';
import PropTypes from 'prop-types';
import { Layout, Menu } from 'antd';
import {
  UserAddOutlined
} from '@ant-design/icons';

import TopBar from '../../layout/topbar/Topbar.container';
import CreateEmployee from '../components/Create.Employee';
import './Dashboard.view.css';

const { Header, Content, Footer, Sider } = Layout;

const Dashboard = ({ isAuthenticated, createEmployee, getAllEmployees, employee, loading, isCreatingSuccess }) => {
  const [state, setState] = useState({collapsed: true})
  const onCollapse = collapsed => {
    setState({ collapsed });
  };
  return (
    <div>
      {isAuthenticated ?
          <Layout style={{ minHeight: '100vh' }}>
        <Sider collapsible collapsed={state.collapsed} onCollapse={onCollapse}>
          <div className="logo" />
          <Menu theme="dark" defaultSelectedKeys={['1']} mode="inline">
            <Menu.Item key="1">
              <UserAddOutlined />
              <span>Add Employee</span>
            </Menu.Item>
          </Menu>
        </Sider>
        <Layout className="site-layout">
          <Header className="site-layout-background" style={{ padding: 15.35, backgroundColor: '#243c50' }}>
            <TopBar />
          </Header>
          <Content style={{ margin: '0 16px' }}>
            <div className="site-layout-background" style={{ padding: 24, minHeight: 360, marginTop: 20 }}>
              <CreateEmployee
                  handleSubmit={createEmployee}
                  handleFetching={getAllEmployees}
                  employeeState={employee}
                  loading={loading}
                  Success={isCreatingSuccess}
              />
            </div>
          </Content>
          <Footer style={{ textAlign: 'center' }}>Ant Design Â©2018 Created by Ant UED</Footer>
        </Layout>
      </Layout>
          : null }

    </div>
  );
};

Dashboard.defaultProps = {
  isAuthenticated: false,
};

Dashboard.propTypes = {
  isAuthenticated: PropTypes.bool,
  createEmployee: PropTypes.func.isRequired,
  getAllEmployees: PropTypes.func.isRequired,
  employee: PropTypes.object,
  loading: PropTypes.bool,
  isCreatingSuccess: PropTypes.bool,
};

Dashboard.styles = {};

export default Dashboard;
