import { get } from 'lodash';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import React, { Fragment, useEffect } from 'react';
import { withRouter, Redirect } from 'react-router-dom';

import AppRoutes from '../../AppRoutes';
import { getUser, refreshToken } from '../auth/auth.api';
import storage from '../../common/storage';
import { AUTH_ROUTES, USER_STATE } from '../../common/constants';

const Layout = ({
  history,
  location,
  getUserFromApi,
  isAuthenticated,
}) => {
  useEffect(() => {
    const interval = setInterval(() => {
      console.log('hello setInterval')
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    if (isAuthenticated && admin() && location && location.pathname) {
      history.push(
        AUTH_ROUTES.includes(location.pathname)
          ? '/dashboard'
          : location.pathname,
      );
    } else if (isAuthenticated && employee() && location && location.pathname) {
      history.push(
          AUTH_ROUTES.includes(location.pathname)
              ? '/employee'
              : '/employee',
      );
    }else if (!isAuthenticated && get(storage.get('user'), 'token')) {
      getUserFromApi()
    }
    // eslint-disable-next-line
  },[getUserFromApi, isAuthenticated, history]);

  const admin = () => get(storage.get('user'), 'user.typeOfId') === USER_STATE.ADMIN_FULL;
  const employee =() => get(storage.get('user'), 'user.typeOfId') === USER_STATE.EMPLOYEE;

  return [
    <Fragment key="redirect">
      {isAuthenticated && (
        <Redirect
          to={
            AUTH_ROUTES.includes(location.pathname)
              ? '/dashboard'
              : location.pathname
          }
        />
      )}
    </Fragment>,
    <div key="app" id="app-wrapper">
      <AppRoutes />
    </div>,
  ];
};

Layout.defaultProps = {
  isAuthenticated: false,
};

Layout.propTypes = {
  isAuthenticated: PropTypes.bool,
  getUser: PropTypes.func,
  history: PropTypes.object.isRequired,
  location: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  isAuthenticated: state.getIn(['auth', 'isAuthenticated']),
});

const mapDispatchToProps = (dispatch) => ({
  getUserFromApi: () => {
    dispatch(getUser());
  },
  refreshToken: () => {
    dispatch(refreshToken());
  }
});

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(Layout));
