import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';

import { UserAddOutlined, FormOutlined, DeleteOutlined, ExclamationCircleOutlined } from '@ant-design/icons';
import { Layout, Form, Row, Col, Input, Spin, InputNumber, Table, Modal, Button, Select, } from "antd";

const { Content } = Layout;
const { confirm } = Modal;
const { Option } = Select;

const layout = {
    labelCol: {
        span: 7,
    },
    wrapperCol: {
        span: 14,
    },
};
const validateMessages = {
    types: {
        email: 'please add a valid email!',
        number: 'please add a number!',
    },
    number: {
        range: 'age must be between 21 and 99',
    },
};

const CUDEmployee = ({
                            handleSubmit,
                            handleFetching,
                            employeeState,
                            loading,
                            Success,
                            handleDeletion,
                            deletion,
                            getSingleEmployee,
                            singleEmployeeState,
                            updateSingleEmployee

 }) => {
    const [visible, setVisible] = useState(false)
    const [visibleUpdate, setVisibleUpdate] = useState(false)
    const [confirmLoading, setConfirmLoading] = useState(false)
    const [search, setSearch] = useState({ words: '' });
    const [page, setPage] = useState({ current: 1 });

    // later i will add condition for different admins
    const typeOfId = 1;
    useEffect((id) => {
        handleFetching();
        if (visibleUpdate === true) {
            getSingleEmployee(id)
        }
    },[handleFetching, Success, deletion, getSingleEmployee])

    console.log('singleEmployeeState', singleEmployeeState)

    let data = (!loading && employeeState ? employeeState.employee.map((row, index) => ({
        Name: row.name.charAt(0).toUpperCase() + row.name.slice(1),
        Gender: row.gender.charAt(0).toUpperCase() + row.gender.slice(1),
        Email: row.email.charAt(0).toUpperCase() + row.email.slice(1),
        Sr: index,
        Id: row._id,
    })): [] );

     data = data.filter((item) =>
        item.Name.toLowerCase().includes(search.words.toLowerCase())
    );

     const empLength = !loading && employeeState ? employeeState.length : 0;

    const [form] = Form.useForm();
    const { validateFields, resetFields, setFieldsValue } = form;

    const showModal = () => {
        setVisible(true)
    };

    const handleOk = () => {
        validateFields()
            .then(values => {
                resetFields();
                onFinish(values);
            })
            .then(() => { setConfirmLoading(true)
                setTimeout(() => {
                    setVisible(false)
                    setConfirmLoading(false)
                }, 1000)})
            .catch(err => {
            console.log(err);
        });

    };

    const handleCancel = () => {
        resetFields();
        setVisible(false)
    };

    const onFinish = values => {
        console.log('data is here', values);
        handleSubmit(values);
    };

    const showDeleteConfirm = (id) => {
        confirm({
            title: 'Are you sure delete this employee?',
            icon: <ExclamationCircleOutlined />,
            content: 'After this operation employee will successfully deleted from database',
            okText: 'Yes',
            okType: 'danger',
            cancelText: 'No',
            onOk() {
                handleDeletion(id)
            },
        });
    }
    const handleOkForUpdate = () => {
        setVisibleUpdate( false )
    };
    const handleCancelForUpdate = () => {
        setVisibleUpdate( false )
    };
    const showModalForUpdate = (id) => {
        setVisibleUpdate( true );
        getSingleEmployee(id)
        setFieldsValue({
            name: 'Hello world!',
            email: 'test@domain.com',
            gender: 'male',
            age: 24,
            designation: 'test123',
            department: 'test12343',
            address: 'test12345',

        });

    };


    const columns = [
        {
            title: "Sr.",
            dataIndex: "Sr",
            key: "Sr"
        },
        {
            title: "Name",
            dataIndex: "Name",
            key: "Name",
            render: (text, record) => <p onClick={() => console.log(record.Id)} style={{cursor: 'pointer', color: 'blue'}}>{text}</p>,
        },
        {
            title: "Gender",
            dataIndex: "Gender",
            key: "Gender"
        },
        {
            title: "Email",
            dataIndex: "Email",
            key: "Email"
        },
        {
            title: "Actions",
            key: "Actions",
            render: (record) => (
                <span style={{ paddingLeft: '10px' }}>
                   <FormOutlined style={{color: 'green'}} onClick={() => showModalForUpdate(record.Id) } />
                    {typeOfId === 1 ?
                        <DeleteOutlined style={{color: 'red', paddingLeft: '15px'}} onClick={() => showDeleteConfirm(record.Id)} />
                        : null }
                </span>
            ),
        },

    ];

    const handleSearch = (e) => {
        setSearch({ words: e.target.value });
        setPage({
            current: 1,
        });
    };

    const onChange = (page) => {
        setPage({
            current: page,
        });
    };

    const showTotal = (total) => {
        return `Total ${total} employees`;
    }
return (
    <Content style={{ margin: "0 16px" }}>
        <div
            className="site-layout-background"
            style={{
                padding: 24,
                minHeight: 360,
                margin: "16px 0"
            }}
        >
            <Row>
                <Col span={7}>
                    <div>
                        <Button type="primary" onClick={showModal}>
                            <UserAddOutlined />
                            Add Employee
                        </Button>
                        <Modal
                            title="Employee Registration Form"
                            visible={visible}
                            onOk={handleOk}
                            okText="Register"
                            confirmLoading={confirmLoading}
                            onCancel={handleCancel}
                            maskClosable={false}
                        >
                                <Form {...layout} form={form} name="nest-messages" onFinish={onFinish} validateMessages={validateMessages}>
                                    <Form.Item
                                        name={'name'}
                                        label="Name"
                                        rules={[
                                            {
                                                required: true,
                                            },
                                        ]}
                                    >
                                        <Input placeholder='Please Input' />
                                    </Form.Item>
                                    <Form.Item
                                        name={'email'}
                                        label="Email"
                                        rules={[
                                            {
                                                type: 'email',
                                                required: true,
                                            },
                                        ]}
                                    >
                                        <Input placeholder='Please enter your email' />
                                    </Form.Item>
                                    <Form.Item
                                        name={'age'}
                                        label="Age"
                                        rules={[
                                            {
                                                type: 'number',
                                                required: true,
                                                min: 21,
                                                max: 99,
                                            },
                                        ]}
                                    >
                                        <InputNumber />
                                    </Form.Item>
                                    <Form.Item
                                        name="gender"
                                        label="Gender"
                                        rules={[
                                            {
                                                required: true,
                                            },
                                        ]}
                                    >
                                        <Select
                                            placeholder="Select a gender"
                                            allowClear
                                        >
                                            <Option value="male">male</Option>
                                            <Option value="female">female</Option>
                                            <Option value="other">other</Option>
                                        </Select>
                                    </Form.Item>
                                    <Form.Item
                                        name={'designation'}
                                        label="Designation"
                                        rules={[
                                            {
                                                required: true,
                                            },
                                        ]}
                                    >
                                        <Input  placeholder='Please enter your designation' />
                                    </Form.Item>
                                    <Form.Item
                                        name={'department'}
                                        label="Department"
                                        rules={[
                                            {
                                                required: true,
                                            },
                                        ]}
                                    >
                                        <Input  placeholder='Please enter your department' />
                                    </Form.Item>
                                    <Form.Item name={'address'} label="Address"   rules={[
                                        {
                                            required: true,
                                        },
                                    ]}>
                                        <Input.TextArea placeholder='Please add address' />
                                    </Form.Item>
                                </Form>
                        </Modal>
                        <Modal
                            title="Update Employee"
                            okText="Update"
                            maskClosable={false}
                            visible={visibleUpdate}
                            onOk={handleOkForUpdate}
                            onCancel={handleCancelForUpdate}
                        >
                            <Form {...layout} form={form} name="nest-messages"  validateMessages={validateMessages}>
                                <Form.Item
                                    name={'name'}
                                    label="Name"
                                    rules={[
                                        {
                                            required: true,
                                        },
                                    ]}
                                >
                                    <Input placeholder='Please Input' />
                                </Form.Item>
                                <Form.Item
                                    name={'email'}
                                    label="Email"
                                    rules={[
                                        {
                                            type: 'email',
                                            required: true,
                                        },
                                    ]}
                                >
                                    <Input placeholder='Please enter your email' />
                                </Form.Item>
                                <Form.Item
                                    name={'age'}
                                    label="Age"
                                    rules={[
                                        {
                                            type: 'number',
                                            required: true,
                                            min: 21,
                                            max: 99,
                                        },
                                    ]}
                                >
                                    <InputNumber />
                                </Form.Item>
                                <Form.Item
                                    name="gender"
                                    label="Gender"
                                    rules={[
                                        {
                                            required: true,
                                        },
                                    ]}
                                >
                                    <Select
                                        placeholder="Select a gender"
                                        allowClear
                                    >
                                        <Option value="male">male</Option>
                                        <Option value="female">female</Option>
                                        <Option value="other">other</Option>
                                    </Select>
                                </Form.Item>
                                <Form.Item
                                    name={'designation'}
                                    label="Designation"
                                    rules={[
                                        {
                                            required: true,
                                        },
                                    ]}
                                >
                                    <Input  placeholder='Please enter your designation' />
                                </Form.Item>
                                <Form.Item
                                    name={'department'}
                                    label="Department"
                                    rules={[
                                        {
                                            required: true,
                                        },
                                    ]}
                                >
                                    <Input  placeholder='Please enter your department' />
                                </Form.Item>
                                <Form.Item name={'address'} label="Address"   rules={[
                                    {
                                        required: true,
                                    },
                                ]}>
                                    <Input.TextArea placeholder='Please add address' />
                                </Form.Item>
                            </Form>
                        </Modal>
                    </div>
                </Col>
                <Col span={12} style={{ paddingLeft: 180 }}>
                    <Form.Item label="Filter By" >
                        <Input placeholder="filter by name" size="small" onChange={handleSearch} />
                    </Form.Item>
                </Col>
            </Row>
            {loading ? (
                <Spin size="large" tip="Loading..." style={{ paddingTop: 100, width: 900 }} />
            ) : (
                <Table
                    rowKey={(obj) => obj.Id}
                    columns={columns}
                    dataSource={data}
                    pagination={{
                        pageSize: 7,
                        current: page.current,
                        onChange: onChange,
                        total: {empLength},
                        showTotal: showTotal
                    }}
                    scroll={{ y: 240 }}
                />
            )}
        </div>
    </Content>
);
}

